package basicdeomofromheadfirstjavabook;

public class DoBee {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1;
		while (num<10) {
			System.out.println("Do");
			System.out.println("Bee");
			num=num+1;
		}
		if (num==10) {
			System.out.println("This DoBeeDo ends here !");
		}
		
	}

}
